% Dire à Matlab que ce n'est pas un fichier d'une seule fonction1;function GUI    % Afficher l'interface utilisateur  msg = {'Veuillez choisir un mode de fonctionnement du programme : '};  button = questdlg(msg, 'Menu principal', 'Démo', 'Projet', 'Quitter', 'Quitter');    if (strcmp(button, 'Démo'))        demo;      elseif (strcmp(button, 'Projet'))        project;
    endif;  endfunction;% Fonctions de mode :function demo    % Appel de la fonction démo  chirp(20, 10, 1, 0.3, 25, 0.5);  endfunction;% ============================================================================% ======================== Séparateur de fonctions ===========================% ============================================================================function project    % Appel de la fonction en mode projet  audio()      endfunction;GUI;